/**
 * Documentation Sidebar Navigation
 * Automatically generates a table of contents from h2 and h3 headings
 * and highlights the current section as you scroll
 *
 * Note: The main sidebar menu is now generated by Hugo partials
 * This script handles the inline TOC that appears under the active page in the sidebar
 */

document.addEventListener('DOMContentLoaded', function() {
  const docsContent = document.querySelector('.docs-content');
  const scrollToTopBtn = document.getElementById('scroll-to-top');

  if (!docsContent) return;

  // Generate inline TOC from headings
  function generateInlineToc() {
    const tocContainer = document.querySelector('.toc-items');
    if (!tocContainer) return; // Exit if inline TOC container doesn't exist

    const headings = docsContent.querySelectorAll('h2, h3');
    if (headings.length === 0) return;

    let currentH2Item = null;
    let currentH3List = null;

    headings.forEach((heading, index) => {
      // Add ID to heading if it doesn't have one
      if (!heading.id) {
        heading.id = `heading-${index}`;
      }

      const link = document.createElement('a');
      link.href = `#${heading.id}`;
      link.textContent = heading.textContent;
      link.className = 'toc-link';

      const listItem = document.createElement('li');
      listItem.className = 'toc-item';
      listItem.appendChild(link);

      if (heading.tagName === 'H2') {
        // Top level item
        tocContainer.appendChild(listItem);
        currentH2Item = listItem;
        currentH3List = null;
      } else if (heading.tagName === 'H3' && currentH2Item) {
        // Nested item under h2
        if (!currentH3List) {
          currentH3List = document.createElement('ul');
          currentH3List.className = 'toc-sublist';
          currentH2Item.appendChild(currentH3List);
        }
        listItem.className = 'toc-item toc-item--sub';
        currentH3List.appendChild(listItem);
      }

      // Smooth scroll on click
      link.addEventListener('click', function(e) {
        e.preventDefault();
        const target = document.getElementById(heading.id);
        if (target) {
          target.scrollIntoView({ behavior: 'smooth', block: 'start' });
          // Update URL without jumping
          history.pushState(null, null, `#${heading.id}`);
          // Update active state
          updateActiveLink(link);
        }
      });
    });
  }

  // Update active link based on scroll position
  function updateActiveLink(activeLink = null) {
    const tocContainer = document.querySelector('.toc-items');
    if (!tocContainer) return; // Exit if inline TOC container doesn't exist

    const links = tocContainer.querySelectorAll('.toc-link');

    if (activeLink) {
      // Manually set active link
      links.forEach(link => link.classList.remove('toc-link--active'));
      activeLink.classList.add('toc-link--active');
      return;
    }

    // Auto-detect based on scroll position
    const headings = docsContent.querySelectorAll('h2, h3');
    let currentHeading = null;

    headings.forEach(heading => {
      const rect = heading.getBoundingClientRect();
      if (rect.top <= 100) {
        currentHeading = heading;
      }
    });

    links.forEach(link => {
      link.classList.remove('toc-link--active');
      if (currentHeading && link.getAttribute('href') === `#${currentHeading.id}`) {
        link.classList.add('toc-link--active');
      }
    });
  }

  // Show/hide scroll to top button
  function toggleScrollButton() {
    if (window.pageYOffset > 300) {
      scrollToTopBtn.classList.add('docs-scroll-btn--visible');
    } else {
      scrollToTopBtn.classList.remove('docs-scroll-btn--visible');
    }
  }

  // Scroll to top functionality
  if (scrollToTopBtn) {
    scrollToTopBtn.addEventListener('click', function() {
      window.scrollTo({ top: 0, behavior: 'smooth' });
    });
  }

  // Initialize - generate inline TOC
  generateInlineToc();
  updateActiveLink();

  // Update on scroll (throttled)
  let scrollTimeout;
  window.addEventListener('scroll', function() {
    if (scrollTimeout) {
      window.cancelAnimationFrame(scrollTimeout);
    }
    scrollTimeout = window.requestAnimationFrame(function() {
      updateActiveLink();
      if (scrollToTopBtn) {
        toggleScrollButton();
      }
    });
  });

  // Handle initial hash in URL
  if (window.location.hash) {
    setTimeout(function() {
      const target = document.querySelector(window.location.hash);
      if (target) {
        target.scrollIntoView({ behavior: 'smooth' });
      }
    }, 100);
  }
});

